数组转换成对象
function array2object($array) { if (is_array($array)) { $obj = new StdClass(); foreach ($array as $key =&gt; $val){ $obj-&gt;$key = $val; } } else { $obj = $array; } return $obj; } 对象转换成数组
function object2array($object) { if (is_object($object)) { foreach ($object as $key =&gt; $value) { $array[$key] = $value; } } else { $array = $object; } return $array; }
